// Usamos esta funcion para generar el choco estatico de abajo
function listar_categorias() {
  $json = file_get_contents('https://api.mercadolibre.com/categories/MLA3025');
  $cat = json_decode($json,true);
  foreach ($cat['children_categories'] as $key => $value) {
    $id = $value['id'];
    $name = $value['name'];
    echo '<optgroup label="'.$name.'">';

    // Listar subcategorias
    $json = file_get_contents('https://api.mercadolibre.com/categories/' . $id);
    $subcat = json_decode($json,true);
    foreach ($subcat['children_categories'] as $key2 => $value2) {
      $id = $value2['id'];
      $name = $value2['name'];
      echo '<option data-subtext="'.$id.'">'.$name.'</option>';
    }
  }
}
